char html[] = "<!DOCTYPE html><html><head><meta name='viewport' content='width=device-width, initial-scale=1'><style>*{margin:0;padding: 0;}html,body{background-color: gold;height: 100vh;font-size: 3vw;display: flex;flex-wrap: wrap;justify-content: center;align-items: center;}.switch {position: relative;display: inline-block;width: 6.0rem;height: 3.4rem;margin: 1rem;}.switch input {opacity: 0;width: 0;height: 0;}.slider {position: absolute;cursor: pointer;top: 0;left: 0;right: 0;bottom: 0;background-color: #ccc;-webkit-transition: .4s;transition: .4s;}.slider:before {position: absolute;content: '';height: 2.6rem;width: 2.6rem;left: 0.4rem;bottom: 0.4rem;background-color: white;-webkit-transition: .4s;transition: .4s;}input:checked + .slider {background-color: #2196F3;}input:focus + .slider {box-shadow: 0 0 1px #2196F3;}input:checked + .slider:before {-webkit-transform: translateX(2.6rem);-ms-transform: translateX(2.6rem);transform: translateX(2.6rem);}/* Rounded sliders */.slider.round {border-radius: 3.4rem;}.slider.round:before {border-radius: 50%;}.gpio{font-size: 2.5rem;width: 100%;display: block;text-align: center;};</style></head><body><script>for(var pin in [0,1,2,3,4,5,9,10,12,13,14,15,16]){document.body.innerHTML += `<div class='gpio'>GPIO ${pin}<label class='switch center'><input type='checkbox' onclick='led(this,${pin});'><span class='slider round'></span></label></div>`}function led (el,pin) {var xmlHttp = new XMLHttpRequest();if (el.checked == true){xmlHttp.open('GET', pin, false );}else{xmlHttp.open('GET', pin, false );}xmlHttp.send( null );}</script></body></html>";
