char html[] = "<!DOCTYPE html>\n<html>\n  <head>\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n    <style>\n      *{\n        margin:0;\n        padding: 0;\n      }\n      html,body{\n        background-color: gold;\n        height: 100vh;\n        font-size: 5vw;\n        display: flex;\n        flex-wrap: wrap;\n        justify-content: center;\n        align-items: center;\n      }\n    .switch {\n      position: relative;\n      display: inline-block;\n      width: 6.0rem;\n      height: 3.4rem;\n    }\n    .switch input {\n    opacity: 0;\n    width: 0;\n    height: 0;\n    }\n    .slider {\n    position: absolute;\n    cursor: pointer;\n    top: 0;\n    left: 0;\n    right: 0;\n    bottom: 0;\n    background-color: #ccc;\n    -webkit-transition: .4s;\n    transition: .4s;\n    }\n    .slider:before {\n    position: absolute;\n    content: \"\";\n    height: 2.6rem;\n    width: 2.6rem;\n    left: 0.4rem;\n    bottom: 0.4rem;\n    background-color: white;\n    -webkit-transition: .4s;\n    transition: .4s;\n    }\n    input:checked + .slider {\n    background-color: #2196F3;\n    }\n    input:focus + .slider {\n    box-shadow: 0 0 1px #2196F3;\n    }\n    input:checked + .slider:before {\n    -webkit-transform: translateX(2.6rem);\n    -ms-transform: translateX(2.6rem);\n    transform: translateX(2.6rem);\n    }\n    /* Rounded sliders */\n    .slider.round {\n      border-radius: 3.4rem;\n    }\n    .slider.round:before {\n    border-radius: 50%;\n    }\n    .center{\n/*      margin-left: 50%;\n      transform: translate(-50%,0);*/\n    }\n    div{\n      font-size: 2rem;\n      width: 100%;\n      display: block;\n      text-align: center;\n    };\n    </style>\n  </head>\n  <body>\n    <div class=\"center\">Led</div>\n    <label class=\"switch center\">\n      <input type=\"checkbox\" checked onclick=\"led(this);\">\n      <span class=\"slider round\"></span>\n    </label>\n    <script>\n      function led (el) {\n        var xmlHttp = new XMLHttpRequest();\n        if (el.checked == true){\n           xmlHttp.open( \"GET\", \"on\", false ); \n        }\n        else{\n          xmlHttp.open( \"GET\", \"off\", false ); \n        }\n        xmlHttp.send( null );\n      }\n    </script>\n  </body>\n</html>";
